mapper.insert:
insert into t_banner_slide (id, picUrl, sequence, 
  redirectType, redirectAddr, description, 
  delFlag, platform, creTime
  )
values (#{id,jdbcType=INTEGER}, #{picUrl,jdbcType=VARCHAR}, #{sequence,jdbcType=TINYINT}, 
  #{redirectType,jdbcType=TINYINT}, #{redirectAddr,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, 
  #{delFlag,jdbcType=TINYINT}, #{platform,jdbcType=TINYINT}, #{creTime,jdbcType=TIMESTAMP}
  )

mapper.select:
  		select * from t_banner_slide 

  			delFlag = 0  

				and	platform = #{platform}
