mapper.update:
update t_patient
set userId = #{userId,jdbcType=INTEGER},
  weight = #{weight,jdbcType=DOUBLE},
  score = #{score,jdbcType=INTEGER},
  illProfile = #{illProfile,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
 	select u.username,u.name,date_format(u.registerTime, '%Y-%m-%d %k:%i') as registerTime 
	from t_user u left join t_patient p on u.id=p.userId order by registerTime
	limit #{start},#{limit}

select  count(*) from t_doctor_patient 

	doctorId = #{doctorId} and patientId = #{patientId} 

		and checked = 0


		and (checked = 1 or checked = 4)


		and relation = #{relation}

select w.id, w.userId, u.name patientName, w.content, date_format(w.postTime, '%Y-%m-%d %k:%i') as postTime, w.isComeTrue, u.mobile, wr.remark from p_wish w left join t_user u on w.userId=u.id left join p_wish_remark wr on w.id=wr.wishId
u.name = #{wish.patientName} and isComeTrue = #{wish.isComeTrue} and u.mobile = #{wish.mobile}
limit #{start},#{limit}

select count(*)
from p_wish w left join t_user u on w.userId=u.id

	patientName = #{wish.patientName}
	 and isComeTrue = #{wish.isComeTrue}
	 and u.mobile = #{wish.mobile}

 	select 
	 	d.id,
	 	u,name,
	 	d.content,
	 	date_format(d.postTime, '%Y-%m-%d %k:%i') as postTime,
	 	di.imageUrl
 	from p_discovery d left join t_user u on d.userId=u.id left join p_discovery_image di on d.id=di.discoveryId 

 		u.name = #{discovery.name}
 		 and d.postTime = #{discovery.postTime}

	limit #{start},#{limit}

select count(*)
from p_discovery d left join t_user u on d.userId=u.id 

	u.name = #{discovery.name}
	 and d.postTime = #{discovery.postTime}
